import { Container, Row, Col, ProgressBar } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect, useState } from 'react'
import history from '../history'


function Home() {

    let user = useSelector((state) => state.user)
    let games = useSelector((state) => state.games)

    useEffect(() => {
        let enemyProgress = document.querySelectorAll(".progress-bar")[0]
        let userProgress = document.querySelectorAll(".progress-bar")[1]
        let gamesWon = games.map(game => game.game_won?game:null).length
        let gamesLost = (games.length) - gamesWon 

        enemyProgress.style.height = `${gamesLost*10}%`
        userProgress.style.height = `${gamesLost*10}%`
    })

    return (
        <Container className='home' fluid >
            <h1>Hello {user.username}</h1>
            <br></br>
            <label>Defeat</label>
            <ProgressBar className="progress-bar-vertical" srOnly variant='danger' />
            <br></br>
            <label>Victory</label>
            <ProgressBar className="progress-bar-vertical" srOnly variant='success' />
        </Container>
    )
}

export default Home;
