export const initialState = {
    //login
    usernameInput:'',
    passwordInput:'',
    user:{},
    ownedCards:[],
    token:'',

    //gameboard
    gameCards: [],
    enemySpace: [],
    enemyGround: [],
    enemyFoot: [],
    userFoot: [],
    userGround: [],
    userSpace: []
}

export const reducer = (state,action) => {
    switch (action.type){

        case 'CHANGE_USERNAME_INPUT':
            return {...state,usernameInput:action.value}
            break;

        case 'CHANGE_PASSWORD_INPUT':
            return {...state,passwordInput:action.value}
            break;

        case 'SET_USER':
            return {...state,user:action.user,token:action.token,ownedCards:action.ownedCards}
            break;

        case 'UPDATE_BOARD':
            let role = `user${action.role}` 
            return {...state, [role]: [role].push(action.card)}
        default: 
            return state
    }
}
