export const initialState = {
    //login
    usernameInput: '',
    passwordInput: '',
    user: {},
    ownedCards: [],
    token: '',

    //gameboard
    gameCards: [],
    enemySpace: [],
    enemyGround: [],
    enemyFoot: [],
    userFoot: [],
    userGround: [],
    userSpace: [],
    hand: []
}

export const reducer = (state, action) => {
    switch (action.type) {

        case 'CHANGE_USERNAME_INPUT':
            return { ...state, usernameInput: action.value }
            break;

        case 'CHANGE_PASSWORD_INPUT':
            return { ...state, passwordInput: action.value }
            break;

        case 'SET_USER':
            return { ...state, user: action.user, token: action.token, ownedCards: action.ownedCards }
            break;

        case 'SET_GAME_CARDS':
            return {...state,gameCards:state.ownedCards}
            break;

        case 'SET_HAND':
            let shuffled = state.gameCards.slice(0), i = state.gameCards.length, temp, index;
            console.log(shuffled)
            while (i--) {
                index = Math.floor((i + 1) * Math.random());
                temp = shuffled[index];
                shuffled[index] = shuffled[i];
                shuffled[i] = temp;
            }
            return shuffled.slice(0, 11);
            break;

        case 'PLAY_CARD':
            //Possible change --> send over something to indicate whether it's the enemy or user 
            let role = `user${action.role}`
            let updatedHand = state.hand.filter(card => card.id !== action.card.id)
            let updatedRow = [...state[role], action.card]
            return { ...state, [role]: updatedRow, hand: updatedHand }
            break;
        default:
            return state
    }
}
