import { Modal, Button, Container, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import history from '../history'

function MoonLostModal(props) {

    let user = useSelector((state) => state.user)


    const deleteAccount = () =>{
        fetch(`http://localhost:3000/users/${user.id}`, {
            method: "DELETE",
            headers: {
                "Authorization": `Bearer ${window.localStorage.token}`
            }
        })
        .then(window.localStorage.token = '')
        .then(history.push('/login'))
    }

    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <Container className='game-won-modal'>
                <Row>
                    <Col><h1 style={{ color: 'green', textAlign: 'center' }}>DEFEAT</h1></Col>
                </Row>
                <Row>
                    <Col style={{ fontSize: '35px', textAlign: 'center' }}>Luna is lost, the only option left is retreat.</Col>
                </Row>
                <Row>
                    <Col><h1 style={{ color: 'green', textAlign: 'center' }}>Maybe one day humanity will return to her....</h1></Col>
                </Row>
                <Modal.Footer >
                    <Button variant='danger' onClick={()=>deleteAccount()} className='game-over-button'>FLEE THE MOON (your account will be destroyed. Sorry!)</Button>
                </Modal.Footer>
            </Container>
        </Modal>
    );
}

export default MoonLostModal
