import { Card } from 'react-bootstrap'
import {useSelector,useDispatch} from 'react-redux'

const UnitCard = ({ card }) => {

    const dispatch = useDispatch()
    const playCard = () => {
        let role = capitalize(card.role)
        useDispatch({type:"UPDATE_BOARD", role:role, card:card})
    }

    const capitalize = (str) => {
        const lower =  str.toLowerCase()
        return str.charAt(0).toUpperCase() + lower.slice(1)
    }

    return (
        <Card className="unit-card" onClick={playCard}>
            <Card.Img variant='top' src={card.image} />
            <Card.ImgOverlay>
                <Card.Text className="strength">{card.strength}</Card.Text>
                <Card.Text className="role">{card.role}</Card.Text>
            </Card.ImgOverlay>
            {/* <Card.Body className="text-center">
                <Card.Title>{card.name}</Card.Title>
                <Card.Subtitle>{card.ability}</Card.Subtitle>
                <Card.Text>{card.flavor_text}</Card.Text>
            </Card.Body> */}
        </Card>
    )
}

export default UnitCard;
