import { Card } from 'react-bootstrap'

const UnitCard = ({ name, image, flavor_text, is_special, role, strength, ability, }) => {
    return (
        <Card>
            <Card.Img variant='top' src={image} />
            <Card.ImgOverlay>
                <p>{strength}</p>
                <p>{role}</p>
            </Card.ImgOverlay>
            <Card.Title>{name}</Card.Title>
            <Card.Subtitle>{ability}</Card.Subtitle>
            <Card.Text>{flavor_text}</Card.Text>
        </Card>
    )
}

export default UnitCard;
